"use strict";angular.module("sbAdminApp").controller("ProfileCtrl",function($scope,$rootScope,$state,UserService){$scope.username=$rootScope.currentUser.username,$scope.firstName=$rootScope.currentUser.firstName,$scope.lastName=$rootScope.currentUser.lastName,$scope.email=$rootScope.currentUser.email;var currentUser=$rootScope.currentUser;$scope.update=function(){var updatedUser={username:$scope.username||currentUser.username,password:$scope.password||currentUser.password,firstName:$scope.firstName||currentUser.firstName,lastName:$scope.lastName||currentUser.lastName,email:$scope.email},callback=function(user){user&&$state.go("dashboard.profile")};UserService.updateUser(currentUser._id,updatedUser,callback)}});