"use strict";angular.module("TutorConnect").controller("ProfileCtrl",function($rootScope,$state,UserService){var vm=this;vm.my=$rootScope.currentUser,vm.update=function(){var updatedUser={username:vm.my.username,password:vm.my.password,firstName:vm.my.firstName,lastName:vm.my.lastName,email:vm.my.email};UserService.updateUser(vm.my._id,updatedUser).then(function(resp){200===resp.status&&$state.go("dashboard.profile")})}});