"use strict";angular.module("TutorConnect").controller("RegisterCtrl",function($rootScope,$scope,$state,UserService){$scope.register=function(){var user={username:$scope.username,password:$scope.password,email:$scope.email,firstName:$scope.first,lastName:$scope.last,roles:[$scope.role]};UserService.createUser(user).then(function(resp){200===resp.status&&($rootScope.currentUser=resp.data,$state.go("dashboard.home"))})}});